#include <iostream>using namespace std;#include <vector>class TreeNode{public:	int data;vector <TreeNode*> children;TreeNode(int data){	this->data = data;}};void print_tree(TreeNode* root){if(root == NULL)	return;cout<<root->data<<": ";for(TreeNode* i : root->children){	cout<<i->data<<",";}cout<<endl;for(TreeNode* i : root->children){	print_tree(i);}}TreeNode* take_input(){	int root_data;	cout<<"enter root data: ";	cin>>root_data;	TreeNode* root_node = new TreeNode(root_data);	cout<<"enter chldrens of root data: ";	int n;	cin>>n;	for(int i = 0 ; i<n ; i++){		TreeNode* child = take_input();		root_node->children.push_back(child);	}	return root_node;}void post_traversal(TreeNode* root_node){	if(root_node->children.size()==0){		cout<<root_node->data<<" ";		return;	}	for(TreeNode* i : root_node->children){		post_traversal(i);	}	cout<<root_node->data<<" ";	return;}void delete_tree(TreeNode* root_node){	for(TreeNode* i : root_node->children){		delete_tree(i);	}	delete root_node;}int main(){	TreeNode* root_node = take_input();	cout<<"post traversal ans: "<<endl;	post_traversal(root_node);}