#include <iostream>using namespace std;#include <vector>class TreeNode{public:	int data;vector <TreeNode*> children;TreeNode(int data){	this->data = data;}};void print_tree(TreeNode* root){if(root == NULL)	return;cout<<root->data<<": ";for(TreeNode* i : root->children){	cout<<i->data<<",";}cout<<endl;for(TreeNode* i : root->children){	print_tree(i);}}int main(){	TreeNode* root = new TreeNode(1);	TreeNode* root1 = new TreeNode(2);	TreeNode* root2 = new TreeNode(3);	root->children.push_back(root1);	root->children.push_back(root2);	print_tree(root);}